import React from "react";
import { Nav, Dropdown, Container } from "react-bootstrap";

// Объект с данными.
// Если для какой-то категории не нужны подкатегории (dropdown),
// задаём пустой массив для неё.
const menuData = {
  "Легковые": ["Новые авто", "Все легковые"],
  "Коммерческие": [],  // изменено: пустой массив, чтобы не было выпадающего меню
  "Спецтехника": [
    "Автокраны",
    "Бульдозеры",
    "Автопогрузчики",
    "Экскаваторы",
    "Сельхоз-техника",
    "Коммунальная техника",
    "Самопогрузчики",
    "Строительная техника"
  ],
  "Мото": [
    "Мотоциклы",
    "Багги",
    "Квадроциклы",
    "Мопеды и скутеры",
    "Картинг",
    "Снегоходы"
  ],
  "Автобизнес": ["Официальные дилеры", "Автобизнес"],
  "Запчасти": [
    "Запчасти и расходники",
    "Шины",
    "Диски",
    "Аксессуары и мультимедиа",
    "Масла и автохимия",
    "Авто на запчасти",
    "Запчасти на коммерческие",
    "Госномера"
  ],
  "Услуги": [
    "СТО, ремонт и обслуживание",
    "Кузовные и малярные работы",
    "Тюнинг и оборудование",
    "Мойка и уход за авто",
    "Портер, грузовые перевозки",
    "Такси, пассажирские перевозки",
    "Эвакуатор",
    "Прокат, аренда",
    "Вскрытие авто, изготовление ключей",
    "Другие автоуслуги"
  ],
  "Куплю": [],
  "Проверить авто": []
};

const CategoriesMenu = ({ onSelectCategory }) => {
  return (
    <Container className="mt-3">
      <Nav variant="pills">
        {Object.entries(menuData).map(([category, subCategories], index) => {
          // Если подкатегорий нет (либо пустой массив) – обычный пункт меню
          if (!subCategories || subCategories.length === 0) {
            return (
              <Nav.Item key={index} className="me-2">
                <Nav.Link
                  onClick={() =>
                    onSelectCategory ? onSelectCategory(category) : null
                  }
                >
                  {category}
                </Nav.Link>
              </Nav.Item>
            );
          } else {
            // Если подкатегории существуют – создаём dropdown
            return (
              <Dropdown key={index} as={Nav.Item} className="me-2">
                <Dropdown.Toggle as={Nav.Link} style={{ cursor: "pointer" }}>
                  {category}
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  {subCategories.map((sub, idx) => (
                    <Dropdown.Item
                      key={idx}
                      onClick={() =>
                        onSelectCategory
                          ? onSelectCategory(`${category} > ${sub}`)
                          : null
                      }
                    >
                      {sub}
                    </Dropdown.Item>
                  ))}
                </Dropdown.Menu>
              </Dropdown>
            );
          }
        })}
      </Nav>
    </Container>
  );
};

export default CategoriesMenu;
