import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import axios from 'axios';

import Header from './components/Header';
import CategoriesMenu from './components/CategoriesMenu';
import FilterSearch from './components/FilterSearch';
import CarList from './components/CarList';
import CarDetailPage from './components/CarDetailPage';
import LoginPage from './components/LoginPage';

function App() {
  const [cars, setCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/cars/')
      .then(response => {
        setCars(response.data);
        setFilteredCars(response.data);
      })
      .catch(error => {
        console.error('Ошибка при загрузке машин:', error);
      });
  }, []);

  const handleCategorySelect = (category) => {
    const filtered = cars.filter(car =>
      car.brand.toLowerCase().includes(category.toLowerCase())
    );
    setFilteredCars(filtered.length ? filtered : []);
  };

  return (
    <div className="app-container">
      <Header />
      <CategoriesMenu onSelectCategory={handleCategorySelect} />
      <FilterSearch />

      <Routes>
        <Route path="/" element={<CarList cars={filteredCars} />} />
        <Route path="/car/:id" element={<CarDetailPage cars={cars} />} />
        <Route path="/login" element={<LoginPage />} />
      </Routes>
    </div>
  );
}

export default App;
