import React from "react";
import CarCarousel from "./CarCarousel";

const CarCard = ({ car }) => {
  const sampleCar = {
    id: 1,
    brand: "Toyota",
    model: "Camry",
    price: 15000,
    shortDescription: "Удобный седан, идеальный для города.",
    images: [
      { url: process.env.PUBLIC_URL + "/images/camry.webp", caption: "Вид спереди" },
      { url: process.env.PUBLIC_URL + "/images/camry2.webp", caption: "Боковой вид" },
      { url: process.env.PUBLIC_URL + "/images/camry3.webp", caption: "Интерьер" }
    ]
  };

  const carData = car || sampleCar;

  return (
    <div style={{ textAlign: "center", marginBottom: "20px" }}>
      <CarCarousel
        images={Array.isArray(carData.images) ? carData.images : []}
        carId={carData.id}
        description={carData.shortDescription}
      />
      <h5 style={{ marginTop: "20px" }}>
        {carData.brand} {carData.model}
      </h5>
      <p>{carData.price} USD</p>
    </div>
  );
};

export default CarCard;
