import React from "react";
import { Carousel } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const CarCarousel = ({ images, carId, description }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/car/${carId}`);
  };

  return (
    <div
      className="carousel-container"
      onClick={handleClick}
      style={{ cursor: "pointer", textAlign: "center" }}
    >
      <Carousel indicators={false} controls interval={null}>
        {images.map((img, index) => (
          <Carousel.Item key={index}>
            <img
              style={{
                width: "137px",
                height: "108px",
                objectFit: "cover",
                margin: "0 auto",
                display: "block"
              }}
              className="d-block"
              src={img.url}
              alt={`Slide ${index + 1}`}
            />
          </Carousel.Item>
        ))}
      </Carousel>
      {description && (
        <div style={{ marginTop: "6px", fontSize: "0.9em" }}>
          {description}
        </div>
      )}
    </div>
  );
};

export default CarCarousel;
