import React from "react";
import { Carousel } from "react-bootstrap";
// import { useHistory } from "react-router-dom"; // - удалено

const CarCarousel = ({ images, carId, description }) => {
  // Если не используешь роутинг, можно убрать useHistory
  // const history = useHistory();

  // Убрать функцию перехода (или заменить на другую логику)
  const handleClick = () => {
    // Например, просто вывод в консоль
    console.log(`Перейти на подробное описание машины ${carId}`);
    // Если хочешь использовать переход, раскомментируй ниже и убедись, что react-router-dom установлен:
    // history.push(`/car/${carId}`);
  };

  return (
    <div onClick={handleClick} style={{ cursor: "pointer" }}>
      <Carousel indicators controls>
        {images.map((img, index) => (
          <Carousel.Item key={index}>
            <div className="carousel-image-wrapper">
              <img
                className="d-block w-100"
                src={img.url}
                alt={`Slide ${index + 1}`}
              />
              {img.caption && (
                <div className="carousel-image-overlay">
                  {img.caption}
                </div>
              )}
            </div>
          </Carousel.Item>
        ))}
      </Carousel>
      {description && (
        <div style={{ marginTop: "10px", textAlign: "center" }}>
          {description}
        </div>
      )}
    </div>
  );
};

export default CarCarousel;
