import React, { useState } from "react";
import { Carousel } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";

const CarDetailPage = ({ cars }) => {
  const { id } = useParams();
  const car = cars.find((c) => c.id === Number(id));
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);

  // Если автомобиль не найден — выводим сообщение.
  if (!car) {
    return <div>Автомобиль не найден</div>;
  }

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  // Если требуется навигация при клике по изображению, проверяем, что клик именно по <img>
  const handleImageClick = (e) => {
    if (e.target.tagName.toLowerCase() === "img") {
      // Например, можно перейти на главную страницу или выполнить другую логику
      navigate("/");
    }
  };

  return (
    <div className="car-detail-page" style={{ textAlign: "center" }}>
      <h1>
        {car.brand} {car.model}
      </h1>
      <div onClick={handleImageClick} style={{ cursor: "pointer" }}>
        <Carousel
          activeIndex={index}
          onSelect={handleSelect}
          indicators={false}
          controls
          interval={null}
        >
          {car.images.map((img, idx) => (
            <Carousel.Item key={idx}>
              <img
                src={img.url}
                alt={`Slide ${idx + 1}`}
                style={{
                  width: "100%",
                  maxWidth: "500px",
                  height: "auto",
                  display: "block",
                  margin: "0 auto",
                  objectFit: "cover",
                }}
              />
            </Carousel.Item>
          ))}
        </Carousel>
      </div>
      {car.details && (
        <div style={{ marginTop: "20px", textAlign: "left", maxWidth: "500px", margin: "20px auto" }}>
          <p><strong>Год выпуска:</strong> {car.details.year}</p>
          <p><strong>Пробег:</strong> {car.details.mileage}</p>
          <p><strong>Кузов:</strong> {car.details.body}</p>
          <p><strong>Цвет:</strong> {car.details.color}</p>
          {/* Добавьте другие детали по необходимости */}
        </div>
      )}
    </div>
  );
};

export default CarDetailPage;
