import React, { useState } from "react";
import Header from "./components/Header";
import CategoriesMenu from "./components/CategoriesMenu";
import FilterSearch from "./components/FilterSearch";
import CarList from "./components/CarList";

function App() {
  const [selectedCategory, setSelectedCategory] = useState("");

  const handleSelectCategory = (category) => {
    console.log("Выбрана категория:", category);
    setSelectedCategory(category);
    // Дополнительная логика фильтрации по категории...
  };

  const handleSearch = (formData) => {
    console.log("Фильтры:", formData);
    // Обработка формы поиска...
  };

  return (
    <div>
      <Header />
      <CategoriesMenu onSelectCategory={handleSelectCategory} />
      <FilterSearch onSearch={handleSearch} />
      <CarList filters={{ category: selectedCategory }} />
    </div>
  );
}

export default App;
