import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./CarDetailPage.css";

const CarDetailPage = ({ cars }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const car = cars.find(c => c.id === parseInt(id));
  const [currentIndex, setCurrentIndex] = useState(0);

  if (!car) return <div>Авто не найден</div>;

  const currentImage = car.images[currentIndex];

  // Останавливаем всплытие события, чтобы клики на стрелках или миниатюрах
  // не приводили к активации каких-либо других навигационных обработчиков.
  const prevSlide = (e) => {
    e.stopPropagation();
    setCurrentIndex((currentIndex - 1 + car.images.length) % car.images.length);
  };

  const nextSlide = (e) => {
    e.stopPropagation();
    setCurrentIndex((currentIndex + 1) % car.images.length);
  };

  const selectSlide = (index, e) => {
    e.stopPropagation();
    setCurrentIndex(index);
  };

  return (
    <div className="detail-wrapper">
      <button
        className="back-button"
        onClick={() => navigate(-1)}
      >
        ← Назад
      </button>
      <div className="main-content">
        <div className="left-info">
          <h3>{car.brand} {car.model}</h3>
          <p><b>Цена:</b> {car.price} USD</p>
          <ul>
            <li><b>Год:</b> {car.details.year}</li>
            <li><b>Пробег:</b> {car.details.mileage} км</li>
            <li><b>Кузов:</b> {car.details.body}</li>
            <li><b>Цвет:</b> {car.details.color}</li>
            <li><b>Двигатель:</b> {car.details.engine}</li>
            <li><b>Коробка:</b> {car.details.gearbox}</li>
            <li><b>Привод:</b> {car.details.drive}</li>
            <li><b>Руль:</b> {car.details.wheel}</li>
            <li><b>Состояние:</b> {car.details.condition}</li>
            <li><b>Таможня:</b> {car.details.customs}</li>
            <li><b>Обмен:</b> {car.details.exchange}</li>
            <li><b>Наличие:</b> {car.details.availability}</li>
            <li><b>Город:</b> {car.details.location}</li>
          </ul>
        </div>

        <div className="right-gallery">
          <div className="image-viewer">
            <button
              className="arrow left"
              onClick={prevSlide}
              // Не даём событию всплыть, чтобы случайно не вызвать контейнерный onClick.
            >
              ‹
            </button>
            <img
              src={currentImage.url}
              alt="car"
              className="main-image"
            />
            <button
              className="arrow right"
              onClick={nextSlide}
            >
              ›
            </button>
          </div>

          <div className="thumbnail-row">
            {car.images.map((img, i) => (
              <img
                key={i}
                src={img.url}
                alt={`thumb-${i}`}
                className={`thumb ${i === currentIndex ? "active" : ""}`}
                onClick={(e) => selectSlide(i, e)}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CarDetailPage;
