import React, { useState } from "react";
import { Routes, Route } from "react-router-dom";
import Header from "./components/Header";
import CategoriesMenu from "./components/CategoriesMenu";
import FilterSearch from "./components/FilterSearch";
import CarList from "./components/CarList";
import CarDetailPage from "./components/CarDetailPage";

const sampleCars = [
  {
    id: 1,
    brand: "Toyota",
    model: "Camry",
    price: 15000,
    shortDescription: "Удобный седан, идеальный для города.",
    images: [
      { url: process.env.PUBLIC_URL + "/images/camry.webp", caption: "Вид спереди" },
      { url: process.env.PUBLIC_URL + "/images/camry2.webp", caption: "Боковой вид" },
      { url: process.env.PUBLIC_URL + "/images/camry3.webp", caption: "Интерьер" }
    ],
    details: {
      year: 2019,
      mileage: 140000,
      body: "внедорожник 5 дв.",
      color: "белый",
      engine: "1.6 л / дизель",
      gearbox: "робот",
      drive: "передний",
      wheel: "слева",
      condition: "хорошее",
      customs: "растаможен",
      exchange: "рассмотрю варианты",
      availability: "в наличии",
      location: "Бишкек, Кыргызстан"
    }
  }
];

function App() {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [filteredCars, setFilteredCars] = useState(sampleCars);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);

    // Пример: фильтруем по вхождению бренда в строку категории
    const filtered = sampleCars.filter((car) =>
      category.toLowerCase().includes(car.brand.toLowerCase())
    );

    setFilteredCars(filtered.length > 0 ? filtered : []);
  };

  return (
    <div className="app-container">
      <Header />
      <CategoriesMenu onSelectCategory={handleCategorySelect} />
      <FilterSearch />
      <Routes>
        <Route path="/" element={<CarList cars={filteredCars} />} />
        <Route path="/car/:id" element={<CarDetailPage cars={sampleCars} />} />
      </Routes>
    </div>
  );
}

export default App;
