import React from "react";
import { Carousel } from "react-bootstrap";
import { useHistory } from "react-router-dom"; // если используешь роутинг

const CarCarousel = ({ images, carId, description }) => {
  const history = useHistory();

  const handleClick = () => {
    // Переход на страницу с подробным описанием машины
    history.push(`/car/${carId}`);
  };

  return (
    <div onClick={handleClick} style={{ cursor: "pointer", maxWidth: "600px", margin: "0 auto" }}>
      <Carousel indicators controls>
        {images.map((img, index) => (
          <Carousel.Item key={index}>
            <div className="carousel-image-wrapper">
              <img
                style={{ maxWidth: "100%", height: "auto" }}
                className="d-block"
                src={img.url}
                alt={`Slide ${index + 1}`}
              />
              {img.caption && (
                <div className="carousel-image-overlay">
                  {img.caption}
                </div>
              )}
            </div>
          </Carousel.Item>
        ))}
      </Carousel>
      {description && (
        <div style={{ marginTop: "10px", textAlign: "center" }}>
          {description}
        </div>
      )}
    </div>
  );
};

export default CarCarousel;
