import React from "react";
import { Carousel } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const CarCarousel = ({ images, carId, description }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/car/${carId}`);
  };

  return (
    <div
      className="carousel-container"
      onClick={handleClick}
      style={{
        cursor: "pointer",
        width: "160px", // увеличено для баланса
        textAlign: "center",
        margin: "0 auto"
      }}
    >
      <Carousel
        controls={true}
        indicators={false}
        interval={null}
        fade={false}
      >
        {images.map((img, index) => (
          <Carousel.Item key={index}>
            <img
              src={img.url}
              alt={`Slide ${index}`}
              style={{
                width: "137px",
                height: "108px",
                objectFit: "cover",
                display: "block",
                margin: "0 auto",
                borderRadius: "6px"
              }}
            />
          </Carousel.Item>
        ))}
      </Carousel>

      {description && (
        <div
          style={{
            marginTop: "5px",
            fontSize: "0.8em",
            color: "#444",
            lineHeight: "1.1"
          }}
        >
          {description}
        </div>
      )}
    </div>
  );
};

export default CarCarousel;
