import React, { useState } from "react";
import "./App.css";
import Header from "./components/Header";
import CategoriesMenu from "./components/CategoriesMenu";
import FilterSearch from "./components/FilterSearch";
import CarList from "./components/CarList";

function App() {
  const [selectedCategory, setSelectedCategory] = useState("");

  // Для теста создаём массив авто
  const sampleCars = [
    {
      id: 1,
      brand: "Toyota",
      model: "Camry",
      price: 15000,
      shortDescription: "Удобный седан, идеальный для города.",
      images: [
        { url: process.env.PUBLIC_URL + "/images/camry.webp", caption: "Вид спереди" },
        { url: process.env.PUBLIC_URL + "/images/camry2.webp", caption: "Боковой вид" },
        { url: process.env.PUBLIC_URL + "/images/camry3.webp", caption: "Интерьер" }
      ]
    },
    // Дополнительные объекты можно добавить здесь
  ];

  return (
    <div className="app-container">
      <Header />
      <CategoriesMenu onSelectCategory={setSelectedCategory} />
      <FilterSearch />
      <div className="main-container">
        <CarList cars={sampleCars} />
      </div>
    </div>
  );
}

export default App;
